CREATE TABLE IF NOT EXISTS `culture` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL,
  `shortDesc` varchar(1000) NOT NULL,
  `smallImageURL` varchar(100) NOT NULL,
  `bigImageURL` varchar(100) NOT NULL,
  `longDesc` varchar(4000) NOT NULL,
  `latitude` double(9,6) NOT NULL,
  `longitude` double(9,6) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

--
-- Zrzut danych tabeli `culture`
--

INSERT INTO `culture` (`id`, `title`, `shortDesc`, `smallImageURL`, `bigImageURL`, `longDesc`, `latitude`, `longitude`) VALUES
(1, 'Capitol', 'Teatr Capitol ma w repertuarze spektakle dla widzów dorosłych i dla dzieci. Od przedstawień rozrywkowych i klasycznych broadwayowskich musicali po oryginalne widowiska, angażujące w inteligentny dialog z twórcami.', 'http://zwiedzamwroclaw.prv.pl/Culture/small/capitol.png', 'http://zwiedzamwroclaw.prv.pl/Culture/big/capitol.jpg', 'Teatr Muzyczny Capitol we Wrocławiu działa w miejscu, przed II wojną funkcjonował kinoteatr Capitol - jeden z największych w ówczesnych Niemczech. Po 1945 r. w częściowo odbudowanym budynku działał Teatr Muzyczny - Operetka Wrocławska, który po 2000 r. przekształcił się w Teatr Muzyczny Capitol. W wyniku gruntownej przebudowy w latach 2011-2013 bryła budynku rozrosła się czterokrotnie, teatr zyskał najnowocześniejszą technologię sceny, a jego – najpiękniejsze w Polsce – modernistyczne wnętrze odrestaurowano z niezwykłą pieczołowitością.', 51.100800, 17.029951),
(2, 'Barbara', 'Barbara to centrum informacji o programie Europejskiej Stolicy Kultury, ale przede wszystkim miejsce spotkań wrocławian. Powstała w miejsce dawnego Baru Barbara.', 'http://zwiedzamwroclaw.prv.pl/Culture/small/barbara.png', 'http://zwiedzamwroclaw.prv.pl/Culture/big/barbara.jpg', 'W nowocześnie zaprojektowanym wnętrzu znajduje się kawiarnia z menu opartym o produkty z regionu Dolnego Śląska, z kącikiem dla dzieci, otwartą także dla zwierząt. Barbara to także przestrzeń na działania kulturalne, od wystaw przez pokazy filmowe i spektakle, a także warsztaty i luźną wymianę myśli', 51.107576, 17.032775),
(3, 'Narodowe Forum Muzyki', 'Narodowe Forum Muzyki to nowoczesny kompleks sal koncertowych oraz siedziba wrocławskiej orkiestry symfonicznej, a także miejsce wielu znaczących wydarzeń kulturalnych.', 'http://zwiedzamwroclaw.prv.pl/Culture/small/narodowe_forum_muzyki.png', 'http://zwiedzamwroclaw.prv.pl/Culture/big/narodowe_forum_muzyki.jpg', 'Narodowe Forum Muzyki powstało na zamówienie miasta Wrocławia. W budynku na placu Wolności inspirowanym kształtem skrzypiec - zaprojektowanym przez wybitnego architekta Stefana Kuryłowicza, znajdują się cztery sale koncertowe z doskonałym systemem nagłośnienia (główna sala ma 1800 miejsc), studio nagraniowe, pomieszczenia wystawiennicze, sale konferencyjne. Przy okazji budowy NFM pod płytą pl. Wolności powstał wielopoziomowy parking samochodowy. Gmach NFM został otwarty we wrześniu 2015 roku. Dzisiaj jest siedzibą Orkiestry Filharmonii Wrocławskiej i jedenastu zespołów instrumentalnych i wokalnych. To także główne miejsce koncertów festiwalu Wratislavia Cantans i wielu innych wydarzeń muzycznych i kulturalnych. M.in gali Europejskiej Nagrody Filmowej w grudniu 2016 roku.', 51.107063, 17.026330),
(4, 'Opera Wrocławska', 'Opera Wrocławska jest jednym z największych i najbardziej znaczących teatrów operowych w Polsce.', 'http://zwiedzamwroclaw.prv.pl/Culture/small/opera_wroclawska.png', 'http://zwiedzamwroclaw.prv.pl/Culture/big/opera_wroclawska.jpg', 'Obiekt wzniesiono w latach 1839-1841 wg projektu K.F. Langhansa i był wielokrotnie przebudowywany. Teatr otwarto 13 listopada 1841 r. 8 września 1945 r. wystawiono tu pierwszy po wojnie spektakl operowy – „Halkę” Stanisława Moniuszki. Jako jedyny teatr operowy w Polsce od 1997 r. realizuje tzw. superwidowiska, spektakle przygotowywane w niekonwencjonalnych, dużych przestrzeniach dla wielotysięcznej widowni, z udziałem kilkusetosobowych zespołów wykonawczych, przy wykorzystaniu monumentalnych dekoracji i efektów specjalnych. Najczęściej do tych przedsięwzięć wykorzystywane są zabytkowe wnętrza  Hali Stulecia. Opera Wrocławska realizuje także, podobne pod względem rozmachu inscenizacyjnego, przedstawienia plenerowe. Największym wydarzeniem było wystawienie na rzece Odrze w czerwcu 2003 r. „Giocondy”. Sześć spektakli obejrzało wtedy łącznie ponad 30 tysięcy widzów.', 51.105612, 17.030567),
(5, 'Teatr Lalek', 'Wrocławski Teatr Lalek mieści się w pięknym neobarokowym budynku. Poza trzema scenami stałymi dysponuje sezonową Sceną Letnią oraz mobilną sceną plenerową - Bajkobusem.', 'http://zwiedzamwroclaw.prv.pl/Culture/small/teatr_lalek.png', 'http://zwiedzamwroclaw.prv.pl/Culture/big/teatr_lalek.jpg', 'Wrocławski Teatr Lalek ma w swoim repertuarze adaptacje utworów z kanonu literatury dziecięcej ("Och, Emil” Astrid Lindgren, „Konik Garbusek” Piotra Jerszowa) i klasycznych baśni - o Pięknej i Bestii, Czerwonym Kapturku, Kopciuszku, Kocie w Butach. Teatr Lalek gra jednak nie tylko klasykę – ostatnie premiery to w większości przedstawienia powstałe w oparciu o współczesne sztuki napisane specjalnie dla Wrocławskiego Teatru Lalek. Są to nowoczesne spektakle o ogromnym potencjale edukacyjnym, wykraczające poza konwencję tradycyjnego teatru lalkowego. Wśród nich są widowiska dla niemowląt, kilkulatków, nieco starszych dzieci oraz rodziców.', 51.105196, 17.033084),
(6, 'Centrum Historii Zajezdnia', 'Miejsce nowoczesnych wystaw historycznych, projektów edukacyjnych i kulturalnych. Wystawa główna zaaranżowana w hali zajezdni jest poświęcona całej powojennej historii Wrocławia, Dolnego Śląska.', 'http://zwiedzamwroclaw.prv.pl/Culture/small/centrum_historii_zajezdnia.png', 'http://zwiedzamwroclaw.prv.pl/Culture/big/centrum_historii_zajezdnia.jpg', 'Wystawa „Wrocław 1945–2016” jest opowieścią o powojennych losach miasta i jego mieszkańców, którzy przybywszy z różnych stron Polski i Kresów od nowa tworzyli społeczną, kulturową i naukową tkankę miasta. Scenograficzna, interaktywna i multimedialna wystawa wzbogacona jest tysiącami zdjęć, materiałami audio-video oraz oryginalnymi i wyjątkowymi eksponatami. Zwiedzający mogą m.in. wysłuchać relacji świadków historii, wystartować w Wyścigu Pokoju, podpisać się pod Orędziem biskupów polskich do biskupów niemieckich, sprawdzić co trzymał w szufladzie Tadeusz Różewicz czy wejść w rolę opozycyjnego drukarza. Centrum Zajezdnia to miejsce nowoczesnych wystaw historycznych, projektów edukacyjnych i kulturalnych. Wystawa główna zaaranżowana w hali zajezdni jest poświęcona całej powojennej historii Wrocławia, Dolnego Śląska i innych regionów włączonych do Polski w wyniku II wojny światowej. W tworzeniu wystaw wykorzystywane są  najnowocześniejsze i innowacyjne techniki ekspozycyjne, atrakcyjne i angażujące zwiedzających.', 51.096531, 16.991151);

