--
-- Struktura tabeli dla tabeli `recreation`
--

CREATE TABLE IF NOT EXISTS `recreation` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL,
  `shortDesc` varchar(1000) NOT NULL,
  `smallImageURL` varchar(100) NOT NULL,
  `bigImageURL` varchar(100) NOT NULL,
  `longDesc` varchar(4000) NOT NULL,
  `latitude` double(9,6) NOT NULL,
  `longitude` double(9,6) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `id` (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=9 ;

--
-- Zrzut danych tabeli `recreation`
--

INSERT INTO `recreation` (`id`, `title`, `shortDesc`, `smallImageURL`, `bigImageURL`, `longDesc`, `latitude`, `longitude`) VALUES
(1, 'Zoo Wrocław', 'Zoo we Wrocławiu jest najstarsze w Polsce i posiada najbogatszą kolekcję zwierząt. Żyje tu 14 tysięcy zwierząt, przedstawicieli tysiąca gatunków. Największą atrakcją Zoo jest Afrykarium, oceanarium wyjątkowe w skali światowej.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/zoo_wroclaw.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/zoo_wroclaw.jpg', 'Wizyta w Ogrodzie Zoologicznym to obowiązkowy punkt pobytu we Wrocławiu. W ciągu kilku godzin mamy szansę poznać na własne oczy rzadkie i oryginalne zwierzęta ze wszystkich kontynentów. Założony w 1865 r. ogród zoologiczny pierwotnie zajmował kilkanaście hektarów. Dopiero w latach 50. XX wieku rozrósł się ponaddwukrotnie. Dzisiaj zajmuje 33 hektary. Przez kilkadziesiąt lat wrocławskim zoo zarządzali Hanna i Antoni Gucwińscy, którzy o obyczajach mieszkańców ogrodu opowiadali w popularnym programie „Z kamerą wśród zwierząt”. Od 2007 r. szefem ogrodu jest Radosław Ratajszczak, który zainicjował wiele pozytywnych zmian. Dzisiaj Zoo Wrocław to nie tylko miejsce spotkania z egzotycznymi zwierzętami. Zoo jest zaangażowane w ratowanie i ochronę wielu zagrożonych gatunków. W ciągu kilku lat powstały nowe wybiegi, m.in. dla niedźwiedzi brunatnych, gibonów, pand małych, nosorożców. Przybyło także nowych lokatorów, w tym zwierząt wyjątkowo rzadkich, jak okapi, czy manaty. Dzisiaj w Zoo żyje około 14 tysięcy zwierząt reprezentujących ponad tysiąc gatunków.', 51.104247, 17.074186),
(2, 'Ogród Japoński', 'To jedno z najpopularniejszych miejsc spacerów i relaksu. Oprócz kilkuset gatunków oryginalnych roślin, drzew, krzewów i kwiatów, w Ogrodzie Japońskim znajdują się stylizowane na orientalne brama i pawilon herbaciany.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/ogrod_japonski.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/ogrod_japonski.jpg', 'Jedną z atrakcji miejsca jest staw z olbrzymimi karpiami oraz innymi gatunkami ryb. W Ogrodzie Japońskim często organizowane są pokazy parzenia herbaty, koncerty i imprezy plenerowe. Ogród Japoński powstał przy okazji organizowanej we Wrocławiu w 1913 roku Wystawy Światowej. Inicjatorem jego budowy był hrabia Fritz von Hochberg, który zaangażował japońskiego ogrodnika Mankichi Arai. Po imprezie zabudowę rozebrano, jednaka zachowała się część oryginalnej roślinności, układ alejek oraz staw. Pomysł rewitalizacji Ogrodu Japońskiego pojawił się w latach 90. Odbudowa z udziałem specjalistów z Japonii trwała trzy lata, jednak wrocławianie nie cieszyli się długo nowym miejscem. W 1997 roku dwa miesiące po otwarciu Ogród Japoński zniszczyła powódź. Przepadło 70 proc. nowo nasadzonych roślin. Na kolejne otwarcie azjatyckiej enklawy czekać trzeba było do października 1999 r.', 51.109938, 17.080077),
(3, 'Aquapark Wrocław', 'Aquapark Wrocław jest jednym z najlepszych i najchętniej odwiedzanych parków wodnych w Europie, a jednym z największych obiektów rekreacyjno-sportowym w Polsce.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/aquapark_wroclaw.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/aquapark_wroclaw.jpg', 'Aquapark Wrocław istnieje od 2008 roku. To jeden z najlepszych i najchętniej odwiedzanych parków wodnych w Europie, a  jednym z największych obiektów rekreacyjno-sportowych w Polsce. Do dyspozycji są Baseny Rekreacyjne, Basen Sportowy o wymiarach olimpijskich, nowoczesną i wielofunkcyjną strefę Fitness, Saunarium, a w nim kompleks saun i łaźni wraz z ogrodem oraz baseny zewnętrzne. Kompleks basenów rekreacyjnych to doskonałe miejsce dla wszystkich tych, którzy szukają relaksu i aktywnego wypoczynku, ale także beztroskiej rozrywki. Na pierwszą grupę czekają cztery głębokie jacuzzi i zewnętrzny basen solankowy, a na drugą – basen z falami, zewnętrzny basen rekreacyjny, leniwa rzeka, zapierające dech w piersiach zjeżdżalnie.', 51.091067, 17.032275),
(4, 'Bulwar Xawerego Dunikowskiego', 'Jedno z najładniejszych miejsc spacerowych we Wrocławiu. Bulwar Dunikowskiego to świetne miejsce na spotkanie w plenerze i relaks.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/bulwar_xawerego_dunikowskiego.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/bulwar_xawerego_dunikowskiego.jpg', 'Szeroka promenada w pobliżu Hali Targowej, między mostem Pokoju a mostem Piaskowym. Na przeciwko gmachu Akademii Sztuk Pięknych powstał amfiteatr z zejściem do rzeki. Ta część nabrzeża służy także za przystań turystyczna, m.in. dla statków białej floty, dla kajaków i małych łódek. Część amfiteatru ma formę kaskady - w upalne dni można tu bezpiecznie pobrodzić w wodzie. Z bulwaru Dunikowskiego rozciąga się kojący widok na Ostrów Tumski, promenadę Włostowica. Promenada to nie tylko miejsce do spacerowania, szukający wytchnienia przed wielkomiejskim gwarem wylegują się na ławka i trawnikach. Poza tym na bulwarze organizowane są pikniki, koncerty i pokazy. W Zatoce Gondoli od wiosny do jesieni działa wypożyczalnia kajaków, łodzi, stąd także można wyruszyć w objazd niewielkim statkiem. Mniej aktywni odpoczną w restauracji na barce. Tuż obok, na tyłach gmachu Muzeum Narodowego, znajduje się plenerowa galeria rzeźb Magdaleny Abakanowicz.', 51.112104, 17.044091),
(5, 'Stadion Wrocław', 'Oryginalna bryła Stadion Wrocław przypomina chiński lampion. Nowoczesny obiekt to miejsce wielu wydarzeń: sportowych i muzycznych, familijnych i artystycznych.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/stadion_wroclaw.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/stadion_wroclaw.jpg', 'Stadion Wrocław to jedna z aren mistrzostw Europy w piłce nożnej UEFA EURO 2012. Oficjalne otwarcie Stadionu odbyło się we wrześniu 2011 r., a na inaugurację wystąpił popularny brytyjski  piosenkarz George Michael z projektem Symphonica. W tym samym miesiącu na ringu na wrocławskiej arenie walkę o pas mistrza świata stoczyli pięściarze Tomasz Adamek i Witalij Kliczko. Podczas EURO 2012 na Stadionie Wrocław mecze rozegrały reprezentacje Polski, Czech, Rosji i Grecji. Polscy piłkarze regularnie grywają na wrocławskim Stadionie. Na trybunach jest 43 tysięcy 863 miejsc, w tym 102 miejsca dla osób niepełnosprawnych. W sezonie piłkarskim na Stadionie Wrocław swoje mecze rozgrywa WKS Śląsk Wrocław. Stadion to miejsce także wydarzeń nie związanych z futbolem. W ciągu roku organizowanych jest tutaj także wiele wspaniałych koncertów i imprez (targów, pikników, pokazów, spektakli). Na Stadionie Wrocław są także sale konferencyjne i bankietowe, które można wynająć na eventy, wesela, studniówki.', 51.141220, 16.943794),
(6, 'Stara Odra Beach Bar', 'Gorący piasek, wielkie plażowe parasole, dobrze wyposażony bar i pogodna muzyka - Stara Odra Beach Bar przy Mostach Trzebnickich, błyskawicznie zyskała grono miłośników.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/stara_odra_beach_bar.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/stara_odra_beach_bar.jpg', 'Stara Odra Beach Bar leży tuż przy pętli tramwajowej przy ul. Zawalnej. To gwarantuje wygodny dojazd z najodleglejszych dzielnic Wrocławia. I tu sprawdza się przepis na odpoczynek doskonały. Potrzebny jest ciepły piasek, leżaki, plażowe parasole jak z tropików, dobrze zaopatrzony bar i wspaniały widok, w którym przenikają się elementy industrialne (Mosty Trzebnickie) i czysta przyroda. Na plaży jest także strefa zabaw dla dzieci. Do odwiedzin zachęca bogaty program wydarzeń. Oczywiście, goście mogą po prostu wygodnie relaksować się w leżakach w cieniu lub w słońcu. Albo wziąć udział w zajęciach jogi, posłuchać koncertu jazzowego, potańczyć na piasku lub na parkiecie lub podziwiać pokaz fair show. Stara Odra Beach Bar ma niezwykły atut wynikający z położenia. Plaża służy za trybunę główną w zawodach w slalomie na nartach wodnych. Pierwsza tego typu impreza już za nami, ale konkurencja jest w programie lipcowych The World Games. Będą emocje! Propozycji zajęć, spotkań, zabaw ciągle przybywa. Ich zapowiedzi pojawiają się na profilu Stara Odra Beach Bar. Organizatorzy zachęcają: bierzcie stroje kąpielowe, szorty, kąpielówki i przychodźcie, bawcie się. Niech będzie jak w wakacje na Hawajach. Wstęp jest bezpłatny.', 51.133962, 17.039129),
(7, 'Park Juliusza Słowackiego', 'Świątynia zadumy w centrum miasta. Z jednej strony romantyczna poezja Słowackiego, z drugiej – Panorama Racławicka i Muzeum Narodowe. Minimum przestrzeni, maksimum przeżyć.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/park_juliusza_slowackiego.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/park_juliusza_slowackiego.jpg', 'Park Słowackiego jest pośrednikiem pomiędzy miastem a przyrodą. Zmęczeni hałasem mogą szybko uciec od zgiełku głównych ulic i udać się do Parku Słowackiego po zasłużony odpoczynek. A relaksować można się tutaj w cieniu drzew i zabytkowych obiektów. Klony, kasztanowce czy lipy osłonią nas przed słońcem - schronienia udzieli także budynek Muzeum Narodowego, Muzeum Architektury i wnętrze Panoramy Racławickiej. Odwiedzający Park Słowackiego mogą również rozłożyć się z kocem na trawie, przysiąść na jednej z ławek wzdłuż alejek, a niekiedy w wakacje załapać się na niedzielne ćwiczenia z jogi. Zielone tereny parku stanowią też dobry punkt do rozpoczęcia zwiedzania miasta - niedaleko stąd do Promenady Staromiejskiej, ciągnącej się wzdłuż Odry, jak i budynku Muzeum Poczty i Telekomunikacji. W parku znajduje się pomnik patrona - Juliusza Słowacki, a także Pomnik Ofiar Zbrodni Katyńskiej i nastrojowy pomnik "Oczekiwanie". Dojazd między innymi tramwajami linii OL, OP, 2, 4, 10, 33 plus oraz autobusami A, N, 145, 149. Na miejsce można również dotrzeć rowerem miejskim - stojaki z rowerami w pobliżu Parku Słowackiego znajdują się na placu Nowy Targ, w Rynku czy przy ul. Wita Stwosza.', 51.109508, 17.045210),
(8, 'GOjump Park Trampolin', 'GOjump to największy park trampolin w Polsce. Na powierzchni 3,5 tysiąca metrów kw. jest kilkanaście trampolin, skoczni i innych konstrukcji, na których można poszaleć i dobrze się przy tym bawić.', 'http://zwiedzamwroclaw.prv.pl/Recreation/small/gojump_park_trampolin.png', 'http://zwiedzamwroclaw.prv.pl/Recreation/big/gojump_park_trampolin.jpg', 'GOjump przy ul. Długosza 59 we Wrocławiu to największy park trampolin w Polsce (3500 mkw.). Tuż koło areny głównej, na której znajdują się trampoliny (750 m kw.), jest strefa wrzutów do kosza, basen z gąbkami, 40-metrowa megatrampa, boisko do gry w zbijaka i 1,8 metrowa poduchą pneumatyczną, na którą śmiało można skakać. W GOjump Park Trampolin działa także strefa akrobatyczno-cyrkowa i największa na Dolnym Śląsku skocznia dla rowerów, snowboardów, rolek i hulajnóg. Wysokiej klasy wyposażenie (podłoga gimnastyczna, lustra, drabinki, przyrządy do ćwiczenia akrobatki, ścieżka airtrack) umożliwi bezpieczny trening amatorów i zawodowców.', 51.135070, 17.066297);

